INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (73590, 1021, 'TEXT', 'interests.locations.3.description', 'Vestige de forêt humide primaire de basse altitude du sud sauvage, cette forêt a poussé sur une ancienne coulée de lave. Aujourd’hui réduite à une centaine d’hectares, elle abrite une richesse remarquable en espèces végétales, dont de nombreuses espèces endémiques. Si elle est encore dans un bon état de conservation, la disparition de la faune qui disséminait les graines (oiseaux, tortues géantes, chauves-souris) empêche sa régénération naturelle. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (72064, 1042, 'TEXT', 'body.introduction', 'En shimaoré, *Oulanga Na Nyamba* signifie «&nbsp;Environnement et Tortues&nbsp;». L''association œuvre depuis 1998 pour la préservation de la biodiversité mahoraise, en particulier des tortues marines et de leurs habitats.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (72065, 1042, 'TEXT', 'body.bodyElements.7.questionAnswer.response', 'On travaille dessus depuis 2015, **c’est un projet multi-partenarial, attendu par tous les acteurs de l’environnement, du tourisme et par les élus qui nous appuient**. C’est un projet très attractif et aussi très utile pour les tortues.', null, null, null, false);
