INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83104, 1053, 'TEXT', 'body.titre_question.12.paragraph.paragraphText', '<strong>Les menaces</strong><br>
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83105, 1053, 'TEXT', 'body.introduction', 'Rapporter des observations de requins ou de raies, surveiller l’état de santé de coraux, ou encore compter des lucioles: autant d’activités qui composent les Sciences Participatives dans les Outre-mer. Les citoyens ultramarins participent à l’aide de ces programmes et à l’instar de leurs compatriotes hexagonaux, à une meilleure compréhension de la biodiversité française. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83106, 1053, 'TEXT', 'body.titre_question.2.paragraph.paragraphText', 'Les sciences participatives sont définies comme une forme de production scientifique à laquelle <strong> des citoyens, qu’ils soient naturalistes, ou simples amoureux de la nature, écoliers, ou encore retraités, participent de manière active</strong> . Les programmes de sciences participatives sont un moyen d’acquérir des données sur des espèces de faune, de flore et même de fonge, très utiles aux chercheurs pour répondre à des questionnements scientifiques. Elles permettent aussi aux citoyens de développer leurs connaissances sur la biodiversité en général et les moyens de la préserver.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83107, 1053, 'TEXT', 'body.titre_question.14.paragraph.paragraphText', 'L’étude conclut qu’il serait pertinent de développer et promouvoir des programmes qui visent <strong>à sensibiliser la jeunesse des territoires ultra marins.</strong> Bien que les jeunes et scolaires soient très représentés dans les Outre-mer, il n’existe pas beaucoup de programmes qui permettent à ce public d’aller sur le terrain, de mettre la tête sous l’eau, d’observer la nature et de se questionner sur son environnement.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (83108, 1053, 'TEXT', 'body.titre_question.8.paragraph.paragraphText', '<strong>Le financement des programmes </strong>
