INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81748, 1004, 'TEXT', 'understand.text2', 'Ces espèces exotiques envahissantes perturbent les équilibres environnementaux en entrant en compétition avec les espèces locales (pour la nourriture, les sites de reproduction ou de repos, la lumière, etc.), par prédation, introduction de pathogènes, hybridation ou encore en modifiant les paramètres physico-chimiques des habitats.  Elles ont ainsi de lourds impacts sur les milieux et les espèces indigènes, en particulier dans les îles où elles sont à l''origine de nombreuses extinctions.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81749, 1004, 'TEXT', 'understand.text1', 'Une espèce exotique envahissante est une espèce introduite par l’Homme hors de son territoire d’origine et qui présente ensuite, sur son territoire d’introduction, une dispersion et un développement importants, engendrant des impacts environnementaux mais aussi sociaux et économiques. Ces introductions sont parfois volontaires (pour l’agriculture, l’ornementation, comme animaux de compagnie, etc.) ou involontaires (organismes présents dans les marchandises échangées internationalement, dans les eaux de ballast des bateaux, etc.).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81750, 1004, 'TEXT', 'territories.title', 'Nombre d''espèces présentes sur la liste des 100 espèces exotiques les plus envahissantes au monde ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81751, 1004, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81752, 1004, 'TEXT', 'visual.subtitle', 'Cliquez sur l''image pour visualiser quelques espèces introduites parmi les plus impactantes pour la biodiversité des territoires d''outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81753, 1004, 'TEXT', 'understand.title1', 'Des espèces introduites par l''Homme', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81754, 1004, 'TEXT', 'understand.paragraphs.1.text', 'Des processus de biosécurité (désinfection, inspections minutieuses, utilisation de chiens formés à la détection, etc.) sont mis en œuvre par les territoires afin de limiter les risques d’entrée de nouvelles espèces exotiques. Pour les espèces déjà installées, de nombreuses interventions sont réalisées pour tenter de limiter leur propagation et atténuer leurs impacts, voire dans certains cas de les éradiquer pour préserver l''équilibre des écosystèmes et sauvegarder des espèces endémiques menacées.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81755, 1004, 'TEXT', 'understand.title2', 'Une des principales causes d''érosion de la biodiversité dans les îles', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81756, 1004, 'TEXT', 'presentation.descriptionTerritories', 'espèces sur les 100 considérées comme les plus envahissantes au monde sont présentes sur le territoire', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81757, 1004, 'TEXT', 'understand.paragraphs.0.title', 'Une liste mondiale des espèces les plus problématiques', null, null, null, false);
