INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79754, 1022, 'TEXT', 'ecosystems.ecosystems.3.description', 'Espèces pionnières et graminées colonisent ces milieux littoraux souvent fragilisés par les dégradations (travaux, incendies, pâturages). Espèce typique de ces milieux dunaires, l’oyat (ou Ammophile à ligule courte, [*Ammophila breviligulata*](https://inpn.mnhn.fr/espece/cd_nom/761890)) joue un rôle essentiel dans la fixation des dunes et limite l’érosion. En arrière dune, une grande diversité d’espèces indigènes, parfois très rares, cohabitent avec des graminées introduites et naturalisées.', null, null, null, false);
