INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79776, 1022, 'TEXT', 'species.0.description', 'Une vingtaine d''espèces de mammifères marins fréquente les eaux de Saint-Pierre-et-Miquelon, dont deux espèces de phoques : le Phoque veau-marin ([*Phoca vitulina*](https://inpn.mnhn.fr/espece/cd_nom/60811)) et le Phoque gris ([*Halichoerus grypus*](https://inpn.mnhn.fr/espece/cd_nom/60776)). C’est sur les bancs de sable de la lagune du Grand Barachois qu’ils se regroupent en plus grand nombre, mais peuvent être observés partout sur les côtes de l’archipel.
