INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79802, 1022, 'TEXT', 'identity.presentation', 'Au sud de l''île de Terre Neuve (Canada), l''archipel se compose de la petit île de Saint-Pierre, où se concentre la majorité de la population, et des îles de Miquelon et Langlade, plus sauvages et préservées. Situé à la confluence du courant froid du Labrador et des eaux plus chaudes du Gulf Stream, l''archipel présente une biodiversité unique en France et une myriade d''écosystèmes, dont la seule forêt boréale française, de très importantes populations d''oiseaux marins et une grande richesse sous-marine.', null, null, null, false);
