INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79810, 1022, 'TEXT', 'interests.locations.0.description', 'Cette lagune de 1 000 ha est bordée de marais et communique avec la mer par un goulet au sud-est. La lagune et les marais qui la bordent sont d’une importante richesse tant faunistique que floristique. Ses bancs de sable accueillent notamment la plus importante colonie reproductrice de phoques veaux-marins du territoire français. Un plan de gestion du site a été validé en 2011 visant à y protéger les espaces naturels remarquables tout en assurant autant que possible leur ouverture au ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79811, 1022, 'TEXT', 'interests.locations.2.description', 'Cet ilot situé au nord de l’île de Saint-Pierre est un site de nidification remarquable. Il accueille une grande part des populations d’oiseaux marins nicheuses de l’archipel, voire la totalité des couples pour certaines espèces comme l’Océanite cul-blanc ([*Hydrobates leucorhous*](https://inpn.mnhn.fr/espece/cd_nom/790986)) ou le Macareux moine ([*Fratercula arctica*](https://inpn.mnhn.fr/espece/cd_nom/3402)). Pour ces deux espèces, il s’agit de colonies parmi les plus importantes au niveau mondial. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79812, 1022, 'TEXT', 'interests.locations.1.description', 'Cette bande de sable de 12 km de long relie les îles de Miquelon et Langlade depuis la fin du 18<sup>e</sup> siècle. Les dunes colonisées par la végétation sont désormais longées par une piste permettant d''accéder à l''île de Langlade, habitée uniquement en période estivale.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79813, 1022, 'TEXT', 'ecosystems.ecosystems.3.name', 'Les dunes sableuses et cordons de galets', null, null, null, false);
