INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79817, 1022, 'TEXT', 'risks.risks.0.description', 'Les menaces pesant sur la biodiversité de Saint-Pierre-et-Miquelon sont nombreuses. Comme tous les territoires d’outre-mer, l’archipel subit la présence d’espèces exotiques envahissantes terrestres, la Renouée du Japon ([*Reynoutria japonica*](https://inpn.mnhn.fr/espece/cd_nom/117503)) et le Séneçon Jacobée ([*Jacobaea vulgaris*](https://inpn.mnhn.fr/espece/cd_nom/610646)) sont parmi les plus impactant.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81778, 1012, 'TEXT', 'presentation.descriptionTerritories', 'des mangroves font l''objet de mesures de conservation', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81779, 1012, 'TEXT', 'understand.paragraphs.0.title', 'Des statuts de protection variés', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81780, 1012, 'TEXT', 'understand.keyword', 'mangroves', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79818, 1022, 'TEXT', 'ecosystems.ecosystems.2.description', 'La seule forêt boréale du territoire français se trouve à Saint-Pierre-et-Miquelon. Fortement dégradée par les herbivores introduits, cette forêt présente une végétation arborescente dans les vallées abritées mais se limite à une strate arbustive ou rampante dans les secteurs exposés aux vents. Dominée par les conifères, on y retrouve le caractéristique Sapin baumier ([*Abies balsamea*](https://inpn.mnhn.fr/espece/cd_nom/717022)), l’Epinette blanche ([*Picea glauca*](https://inpn.mnhn.fr/espece/cd_nom/611390)), l’épinette noire ([*Picea mariana*](https://inpn.mnhn.fr/espece/cd_nom/762061)), dans certains milieux tourbeux le Mélèze laricin ([*Larix laricina*](https://inpn.mnhn.fr/espece/cd_nom/717287)), seul conifère qui perd ses épines en hiver, ou encore, bien que rare sur Langlade et Miquelon, l''If du Canada ([*Taxus canadensis*](https://inpn.mnhn.fr/espece/cd_nom/717885)) sous sa forme rampante.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79819, 1022, 'TEXT', 'ecosystems.ecosystems.1.description', 'Ces zones humides occupent des superficies importantes. Caractérisées par la formation et l’accumulation de tourbe, parfois sur plusieurs mètres d’épaisseur, elles présentent des groupements végétaux remarquables et variés, parmi lesquelles on trouve des sphaignes, des mousses, ainsi que des orchidées.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81781, 1012, 'TEXT', 'ecogestures.title', 'Que puis-je faire pour aider à préserver les mangroves ? ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81782, 1012, 'IMAGE', 'presentation.image', null, 1047, 'Palétuviers, baie de Grand Cul-de-Sac marin, Guadeloupe © Gaëlle Vandersarren - Comité français UICN', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81783, 1012, 'TEXT', 'understand.paragraphs.1.title', 'Un réseau participatif pour signaler les perturbations dans les mangroves', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81784, 1012, 'IMAGE', 'visual.image', null, 1927, 'Carte mangroves outre-mer', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81785, 1012, 'TEXT', 'visual.title', 'Les mangroves d''outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81786, 1012, 'LINK', 'understand.article', 'aucun', null, null, 'aucun', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81787, 1012, 'TEXT', 'understand.paragraphs.0.text', 'Il existe un éventail de dispositions juridiques permettant de protéger les mangroves, par exemple le classement en Zone humide d’importance internationale par la Convention de Ramsar ou les arrêtés de protection de biotope, et de nombreuses zones de mangroves sont présentes dans les aires protégées des territoires (parcs nationaux, parcs naturels régionaux, réserves naturelles, etc.). Cependant, toutes les mangroves protégées ne font pas forcément l’objet de mesures de gestion.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81788, 1012, 'IMAGE', 'understand.image', null, 1314, 'Sentier aménagé dans la mangrove, Martinique © Fabien Lefebvre - Association ACWAA', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81789, 1012, 'TEXT', 'presentation.description', 'des mangroves nationales font l’objet de mesures de conservation', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81790, 1012, 'TEXT', 'presentation.logoUrl', 'https://naturefrance.fr/indicateurs/surface-de-mangroves-faisant-lobjet-de-mesures-de-conservation', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79820, 1022, 'TEXT', 'ecosystems.ecosystems.0.description', 'Au-dessus des forêts, les végétaux ligneux adoptent un port prostré et laissent place à des landes dominées par les éricacées au port rampant, dont le Kalmia à feuilles étroites ([*Kalmia angustifolia*](https://inpn.mnhn.fr/espece/cd_nom/762256)). Aux altitudes les plus élevées, cette formation laisse place à une toundra adaptée aux conditions très venteuses, typique des milieux alpins ou arctiques.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79821, 1022, 'TEXT', 'identity.highestPoint', '240', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79822, 1022, 'TEXT', 'interests.locations.4.description', 'Chef-lieu du territoire, cette commune concentre 86% de la population de l’archipel. Au début restreinte à la zone portuaire, la ville s’est beaucoup étendue en périphérie ces dernières années, ce qui n’est pas sans causer des problèmes en matière d’assainissement, de gestion des déchets, et de dérangement de la faune aviaire. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79823, 1022, 'TEXT', 'risks.risks.0.name', 'Les ressources naturelles, un enjeu important', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79824, 1022, 'IMAGE', 'interests.locations.1.image', null, 1103, 'Vue aérienne de l''isthme © DR', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79825, 1022, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1104, 'Tourbière près de l’étang de Richepomme © Nathalie de Lacoste', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79826, 1022, 'TEXT', 'interests.locations.3.description', 'Située dans le tissu urbain de la ville de Saint-Pierre, cette enclave de tourbières et étangs de 27 ha est l’une des dernières tourbières de la partie sud de l’île de Saint-Pierre. D’un intérêt écologique fort, en partie car de nombreuses espèces d’oiseaux y font étape durant leur trajet migratoire, le site fait l’objet d’un plan de gestion depuis 2018.
