INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80110, 1051, 'TEXT', 'body.titre_question.0.paragraph.paragraphText', '<strong>La forêt sèche recèle une biodiversité élevée et de nombreuses espèces endémiques. Elle est le lieu de vie d’une faune très variée et joue un rôle de transition entre les forêts du littoral et les forêts humides de la chaîne centrale</strong>. Pour préserver sa biodiversité remarquable et son rôle écologique fort, les acteurs du territoire se mobilisent ! Neuf partenaires (État, Gouvernement de la Nouvelle-Calédonie, province Nord, province Sud, IAC, IRD, UNC, WWF-France et CIE*) sont engagés depuis 2001 pour sa protection et sa restauration, notamment à travers le *Programme de Conservation des Forêts Sèches* (PCFS), coordonné depuis 2012 par le Pôle Forêt Sèche du Conservatoire d’espaces naturels de Nouvelle-Calédonie (CEN).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80111, 1051, 'TEXT', 'presentation.name', 'Un tableau de bord dédié aux forêts sèches de Nouvelle-Calédonie', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80112, 1051, 'TEXT', 'body.titre_question.5.paragraph.paragraphText', '<strong>Consultez les outils numériques dédiés aux forêts sèches de Nouvelle-Calédonie </strong>: [le tableau de bord](https://dtsi-sgt.maps.arcgis.com/apps/opsdashboard/index.html#/03b75c712bfe4089ab9cfd971a2e9aec) et l’[explorateur cartographique des forêts sèches](https://dtsi-sgt.maps.arcgis.com/apps/webappviewer/index.html?id=d4d2b25f57d94ba6918d0a8d26831301).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80113, 1051, 'TEXT', 'body.titre_question.2.citation.citationElement', 'On peut par exemple visualiser en un clic le nombre d’hectares reboisés (51,3 ha) ou la surface de forêt sèche classée en aire protégées (seulement 0,6 %).', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80114, 1051, 'IMAGE', 'presentation.image', null, 1921, 'Forêt sèche de Malhec © Conservatoire d''espaces naturels de Nouvelle-Calédonie', null, false);
