INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80118, 1051, 'TEXT', 'body.titre_question.1.paragraph.paragraphText', '<strong>Le tableau de bord des forêts sèches de Nouvelle-Calédonie est une interface visuelle, en ligne depuis début 2021, qui centralise des données géographiques et renseigne plusieurs indicateurs chiffrés sur les forêts sèches de Nouvelle-Calédonie et les actions de protection et de restauration déployées pour leur conservation</strong>.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80806, 1041, 'TEXT', 'species.7.description', 'Le Phylica ([*Phylica arborea*](https://inpn.mnhn.fr/espece/cd_nom/780147)) est le seul arbre indigène des Terres australes françaises, et il est présent uniquement sur l’île d’Amsterdam. Les incendies et la dégradation par les bovins introduits avaient presque conduit à sa disparition. Il est aujourd’hui au centre d''un programme de restauration mené par la Réserve naturelle nationale des Terres australes françaises qui met en place la plantation de jeunes individus en milieu naturel afin de recréer des boisements sains dans son aire de distribution originelle, après l''éradication des bovins de l''île en 2010.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80807, 1041, 'TEXT', 'species.8.description', 'Les orques ([*Orcinus orca*](https://inpn.mnhn.fr/espece/cd_nom/60905)) sont des super-prédateurs fréquemment observées dans les eaux subantarctiques. Elles vivent en groupes sociaux dirigés par la doyenne des femelles et se nourrissent de poissons, de manchots, d''otaries et d''éléphants de mer ainsi que d''autres cétacés, qu''elles chassent grâce à des techniques élaborées et diverses qui se transmettent de génération en génération. Les femelles deviennent adultes autour de 15 ans, et donnent naissance à un petit tous les 5 ans environs.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80808, 1041, 'IMAGE', 'species.9.image', null, 1740, '*Anatalanta aptera* © TAAF ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80809, 1041, 'TEXT', 'events.2.description', 'Émergence des îles Saint-Paul et Amsterdam', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80810, 1041, 'TEXT', 'events.3.description', 'Découverte des îles Saint-Paul et Amsterdam par le navigateur Sébastian Del Cano et les compagnons de Magellan', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80811, 1041, 'IMAGE', 'species.2.image', null, 1735, '*Eudyptes chrysolophus* © Julie Tucoulet', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80812, 1041, 'IMAGE', 'identity.image', null, 1702, 'Vallée de la Hébé, île de la Possession, Crozet © Julie Tucoulet', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80813, 1041, 'TEXT', 'species.3.description', 'L’Éléphant de mer du sud ([*Mirounga leonina*](https://inpn.mnhn.fr/espece/cd_nom/528709)) est le plus gros représentant de la famille des phoques. Il doit son nom au nez en forme de trompe qui se développe chez les mâles dominants. Sa chasse massive pour sa graisse au cours des 19<sup>e</sup> et 20<sup>e</sup> siècles a mené l’espèce au bord de l’extinction, mais les populations se sont aujourd’hui bien reconstituées. Chaque année, environ 90 000 femelles viennent se reproduire sur les côtes de Kerguelen.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80814, 1041, 'TEXT', 'events.7.description', 'Création de la réserve naturelle nationale des Terres australes françaises', null, null, null, false);
