INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79838, 1044, 'TEXT', 'species.3.description', 'Le krill désigne un ensemble de nombreuses espèces de crustacés d’eaux froides dont l’espèce la plus fréquente, l’[*Euphausia superba*](https://inpn.mnhn.fr/espece/cd_nom/839509), ressemble à une crevette. Au cœur du réseau alimentaire, le krill se nourrit de plancton, abondant dans les eaux polaires, et sert ensuite de nourriture de base aux poissons, baleines, phoques et oiseaux. Le krill représente sans doute la biomasse la plus abondante de la planète, estimée à plusieurs centaines de millions de tonnes.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79839, 1044, 'TEXT', 'events.7.description', 'Ratification du Protocole de Madrid par la France', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79840, 1044, 'TEXT', 'events.7.date', '1998', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79841, 1044, 'TEXT', 'interests.locations.0.name', 'La base Dumont d’Urville', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79842, 1044, 'TEXT', 'species.1.description', 'Le Manchot Adélie ([*Pygoscelis adeliae*](https://inpn.mnhn.fr/espece/cd_nom/442302)) est plus petit et plus léger que son cousin empereur. Il s’en distingue également par l’absence de tache auriculaire jaune, et plonge beaucoup moins profond pour sa recherche de nourriture (entre 20 et 30 m de profondeur maximum). La Terre Adélie compterait autour de 175 000 couples reproducteurs, soit 4,6 % de la population mondiale.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79843, 1044, 'TEXT', 'species.2.description', 'Le Phoque de Weddell ([*Leptonychotes weddellii*](https://inpn.mnhn.fr/espece/cd_nom/535503)) partage son temps entre la recherche de nourriture (poissons, calamars…) dans l’eau et le repos sur la banquise antarctique. Les femelles mettent bas sur la glace entre début octobre et mi-novembre. En mer, il est vulnérable face au léopard de mer et à l’orque.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79844, 1044, 'TEXT', 'events.0.description', 'Premières expéditions atteignant le continent blanc', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79845, 1044, 'TEXT', 'events.1.description', 'Débarquement de Jules Dumont d’Urville sur le Rocher du Débarquement, qu’il baptise «&nbsp;Terre Adélie&nbsp;»', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79846, 1044, 'TEXT', 'events.8.description', 'Création d''une aire marine protégée internationale sur la mer de Ross. La France défend aujourd’hui la création d’une vaste aire marine protégée en Antarctique Est bordant notamment de la Terre Adélie.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79847, 1044, 'TEXT', 'events.9.description', 'Mise en service de L’Astrolabe, nouveau brise-glace qui assure le ravitaillement de la base Dumont d’Urville depuis l’Australie', null, null, null, false);
