INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79857, 1044, 'TEXT', 'species.4.description', 'Les manchots ne sont pas les seuls à nicher en Terre Adélie : au total 8 espèces d’oiseaux marins se reproduisent sur l’archipel de Pointe Géologie. Outre le Manchot empereur et le Manchot d’Adélie, il s’agit du Pétrel géant antarctique ([*Macronectes giganteus*](https://inpn.mnhn.fr/espece/cd_nom/994)), du Pétrel des neiges ([*Pagodroma nivea*](https://inpn.mnhn.fr/espece/cd_nom/442180)), du Damier du Cap ([*Daption capense*](https://inpn.mnhn.fr/espece/cd_nom/1002)) du Skua antarctique ([*Stercorarius maccormicki*](https://inpn.mnhn.fr/espece/cd_nom/442378)), de l’Océanite de Wilson ([*Oceanites oceanicus*](https://inpn.mnhn.fr/espece/cd_nom/1039)) et du Fulmar antarctique ([*Fulmarus glacialoides*](https://inpn.mnhn.fr/espece/cd_nom/626335)).
