INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79874, 1044, 'TEXT', 'identity.presentation', 'Froid polaire, blizzards, longue nuit polaire, en Terre Adélie, secteur angulaire de l''Antarctique, les conditions de vie sont extrêmes et peu d’espèces sont capables de les supporter ! Le territoire est difficilement accessible : à partir du mois de mars, la mer qui borde le continent Antarctique se couvre d’une pellicule de glace qui s’épaissit pour atteindre un à deux mètres durant l’hiver, rendant la navigation et l’accès impossible. Le retour de l’été entraîne la débâcle de cette glace qui se fragmente en plaques partant à la dérive.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79875, 1044, 'IMAGE', 'interests.locations.0.image', null, 1868, 'Base Dumont d''Urville © TAAF', null, false);
