INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79880, 1044, 'TEXT', 'events.4.date', '1959', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79881, 1044, 'IMAGE', 'interests.locations.2.image', null, 1787, 'Port Martin © Fonds Yves Vallette', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79882, 1044, 'TEXT', 'interests.locations.0.description', 'Située sur l’île des Pétrels, dans l''archipel de Pointe Géologie à 5 km du continent Antarctique, la base héberge une trentaine de personnes (scientifiques et personnel de maintenance) pendant l’hivernage (de mars à novembre), et jusqu’à 88 pendant l’été. Pendant l’hiver austral, les hivernants sont coupés du monde car l’accès n’est possible ni par bateau, ni par voie aérienne. Construite en 1956, la base est opérationnellement gérée par l’Institut polaire français ([IPEV](https://institut-polaire.fr/fr/)).', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79883, 1044, 'TEXT', 'risks.risks.1.description', 'Une trentaine de projets scientifiques sont menés en Terre Adélie chaque année, principalement en sciences de la Vie et de l’Environnement (stratégies de survie des organismes et adaptation des espèces aux conditions extrêmes, effets des changements climatiques…), en sciences de la Terre et de l’Univers (glaciologie, étude de l’atmosphère, géologie, physique du globe…) mais aussi en sciences humaines (biologie humaine et adaptation aux conditions d’hivernage). Ces expéditions sont orchestrées par l’[Institut Polaire Français](https://www.institut-polaire.fr/language/fr/).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79884, 1044, 'TEXT', 'interests.locations.2.description', 'A 60 km de la base Dumont d’Urville se trouvent les vestiges de la base de Port Martin, première station française en Antarctique. Installée en 1950, la base a accueilli les missions scientifiques françaises jusqu’à son incendie accidentel dans la nuit du 23 au 24 janvier 1952. Les vestiges de la base sont placés sous un statut de protection particulier en vue de préserver le patrimoine culturel et historique qui s’y trouve.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79885, 1044, 'TEXT', 'interests.locations.1.description', 'Située à quelques kilomètres de la station Dumont d’Urville, cette base secondaire est occupée uniquement pendant l’été austral. Elle est dédiée à l’organisation des convois terrestres à destination de Concordia, une base franco-italienne implantée à plus de 1 100 km à l’intérieur des terres, sur le secteur angulaire australien.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79886, 1044, 'TEXT', 'statistics.1.text', 'de mammifères marins', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79887, 1044, 'TEXT', 'events.9.date', '2017', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79888, 1044, 'TEXT', 'identity.subtitle', 'L''Antarctique, un sanctuaire dédié à la recherche et à la paix', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79889, 1044, 'IMAGE', 'species.4.image', null, 1808, '*Oceanites oceanicus* © TAAF', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (79890, 1044, 'TEXT', 'risks.risks.0.description', 'Le continent Antarctique est à lui seul l’une des huit écozones de la planète (régions biogéographiques terrestres). Seuls quelques espèces d’oiseaux vivent sur les terres glacées de l’Antarctique et sur les 9 espèces d’oiseaux marins nichant en Antarctique, 8 sont présentes en nidification sur l’archipel de Pointe Géologie, où est installée la Base Dumont d’Urville.
