INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81827, 1009, 'TEXT', 'visual.title', 'Les espèces indigènes d''outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81828, 1009, 'LINK', 'understand.article', 'aucun', null, null, 'aucun', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81829, 1009, 'TEXT', 'understand.paragraphs.0.text', 'Au niveau floristique, les plantes à fleurs présentent le plus grand nombre d’espèces : on en compte 12 306 dans tout l''outre-mer. Mais ce ne sont pas les seules espèces végétales : on compte également plusieurs centaines d’espèces de mousse, de lichens ou encore d’algues.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81830, 1009, 'IMAGE', 'understand.image', null, 1040, 'Iguane des petites Antilles, Martinique © Fabien Lefebvre - Association ACWAA', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81831, 1009, 'TEXT', 'presentation.description', 'espèces sont indigènes dans les outre-mer', null, null, null, false);
