INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80123, 1040, 'TEXT', 'events.2.description', 'Estimation de la première occupation humaine des îles de la Société', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80124, 1040, 'TEXT', 'events.3.description', 'Première arrivée des européens aux Marquises', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81833, 1014, 'SELECT', 'presentation.sourceSelect', 'onb', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81834, 1014, 'TEXT', 'understand.text2', 'Cet indicateur présente l’évolution du recouvrement en corail vivant sur des stations réparties dans les différents territoires d’outre-mer. La situation est en réalité très hétérogène selon les collectivités : les récifs sont ainsi très dégradés en Martinique et Guadeloupe, mais beaucoup moins à Wallis-et-Futuna ou Mayotte. La situation est considérée comme stable pour 60 % des stations étudiées.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81835, 1014, 'TEXT', 'understand.text1', 'Les récifs coralliens sont formés par des colonies de coraux, formées de polypes (petits organismes invertébrés de la même famille que les méduses) sécrétant un squelette calcaire, et vivant en symbiose avec des algues unicellulaires, les zooxanthelles, qui leur apportent des nutriments grâce à la photosynthèse.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81836, 1014, 'TEXT', 'territories.title', 'Pourcentage de surface des récifs coralliens en diminution', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81837, 1014, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81838, 1014, 'TEXT', 'visual.subtitle', 'Visualisez le nombre de stations suivies et leur état de santé dans les outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81839, 1014, 'TEXT', 'understand.title1', 'Le corail, constructeur du récif', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81840, 1014, 'TEXT', 'understand.paragraphs.1.text', 'Créée en 1999, l’[Initiative française pour les récifs coralliens (IFRECOR)](https://ifrecor.fr/) agit pour la protection et la gestion durable des récifs coralliens, des herbiers et des mangroves dans les collectivités françaises d’outre-mer. Chaque collectivité abritant ces écosystèmes possède un comité local rassemblant les acteurs concernés par leur protection et leur gestion durable. L’initiative collective porte un plan d''action sur 5 ans qui couvre 6 axes stratégiques : planifier pour prévenir ; réduire les effets négatifs dus aux activités humaines, tout en assurant leur développement durable ; connaître et comprendre pour gérer ; informer et éduquer pour modifier les comportements ; développer les échanges et la coopération pour renforcer les synergies et valoriser les expériences.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81841, 1014, 'TEXT', 'understand.title2', 'Un état de santé hétérogène selon les territoires', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81842, 1014, 'TEXT', 'presentation.descriptionTerritories', 'des récifs coralliens sont en diminution', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81843, 1014, 'TEXT', 'understand.paragraphs.0.title', 'Les coraux en danger ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81844, 1014, 'TEXT', 'understand.keyword', 'corail', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81845, 1014, 'TEXT', 'ecogestures.title', 'Comment préserver les coraux à mon échelle ? ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81846, 1014, 'IMAGE', 'presentation.image', null, 1053, 'Récif corallien, Parc naturel marin de Mayotte © Marine Dekenen - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81847, 1014, 'TEXT', 'understand.paragraphs.1.title', 'L’IFRECOR, une initiative pour préserver les récifs ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81848, 1014, 'IMAGE', 'visual.image', null, 1929, 'Carte récifs coralliens outre-mer', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81849, 1014, 'TEXT', 'visual.title', 'Les récifs coralliens d''outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81850, 1014, 'LINK', 'understand.article', 'aucun', null, null, 'aucun', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81851, 1014, 'TEXT', 'understand.paragraphs.0.text', 'Les coraux sont des organismes très fragiles : lorsqu’ils sont soumis à un stress, les polypes expulsent leurs algues, ce qui provoque le blanchissement du corail et finit par entrainer sa mort si cette situation est prolongée.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81852, 1014, 'IMAGE', 'understand.image', null, 1309, ' Le stress provoque le blanchissement des coraux (ici à Mayotte) © Marine Dedeken - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81853, 1014, 'TEXT', 'presentation.description', 'des récifs coralliens sont en diminution dans les territoires d''outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81854, 1014, 'TEXT', 'presentation.logoUrl', 'https://naturefrance.fr/indicateurs/evolution-de-letat-des-recifs-coralliens', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80125, 1040, 'IMAGE', 'identity.image', null, 1686, 'Huahine (archipel de la Société) © Céline Soyer', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80126, 1040, 'TEXT', 'events.7.description', 'Création de la réserve de biosphère UNESCO de Fakarava (Tuamotu)', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80127, 1040, 'TEXT', 'events.7.date', '1977', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81855, 1011, 'SELECT', 'presentation.sourceSelect', 'onb', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81856, 1011, 'TEXT', 'understand.text2', 'On qualifie de primaire, ou vierge, une forêt où aucune trace d’activité humaine (défrichement, exploitation, fragmentation), passée ou présente, n’est clairement visible et où les processus écologiques ne sont pas perturbés. A l’échelle mondiale, on estime que plus de 80 % des forêts primaires ont disparu, remplacées par des forêts secondaires moins riches écologiquement, et les reliquats sont de plus en plus réduits et fragmentés.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81857, 1011, 'TEXT', 'understand.text1', 'Les forêts primaires et autres écosystèmes forestiers des outre-mer présentent une biodiversité considérable. Les territoires ultramarins sont cependant très hétérogènes dans leur couverture forestière et leurs caractéristiques : des forêts tropicales humides de Guyane qui couvrent la quasi-totalité du territoire aux forêts boréales de conifères de Saint-Pierre-et-Miquelon, chaque territoire possède une couverture forestière et une biodiversité qui lui est propre. Seuls Saint Barthélemy, les Terres australes françaises et la Terre Adélie ne présentent pas de forêts sur leurs territoires. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81858, 1011, 'TEXT', 'territories.title', 'Pourcentage du territoire occupé par la forêt', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81859, 1011, 'CHECKBOX', 'presentation.shadowed', 'false', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81860, 1011, 'TEXT', 'visual.subtitle', 'Visualisez les taux de boisement et l''étendue des surfaces forestières en outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81861, 1011, 'TEXT', 'understand.title1', 'Les forêts, un réservoir de biodiversité', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81862, 1011, 'TEXT', 'understand.paragraphs.1.text', 'Au-delà de leur rôle pour la préservation de la biodiversité, les forêts fournissent de nombreux services écosystémiques : elles protègent par exemple les sols de l’érosion, préservent les ressources en eau et stockent le carbone. Elles permettent de se fournir en bois de construction et plantes médicinales, bien que ces ressources puissent être surexploitées. Les forêts ont également une grande importance culturelle dans les territoires d''outre-mer, dans les légendes, les arts et le bien-être de la population.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81863, 1011, 'TEXT', 'understand.title2', 'Qu’est-ce qu’une forêt primaire ?', null, null, null, false);
