INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80179, 1040, 'TEXT', 'interests.locations.6.name', 'Fakarava (Tuamotu) ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80180, 1040, 'TEXT', 'species.3.description', 'Le Monarque de Tahiti ([*Pomarea nigra*](https://inpn.mnhn.fr/espece/cd_nom/418875)) est un passereau insectivore forestier, endémique de l’île de Tahiti. L’adulte est entièrement noir, avec un bec et des pattes plus claires, tandis que le jeune est orangé. L’espèce est victime de l’introduction d’espèces exotiques envahissantes, majoritairement de la prédation des œufs par les rats, la compétition avec les oiseaux introduits (Martin triste ([*Acridotheres tristis*](https://inpn.mnhn.fr/espece/cd_nom/601183)) et Bulbul à ventre rouge ([*Pycnonotus cafer*](https://inpn.mnhn.fr/espece/cd_nom/432746))) et la dégradation de son habitat forestier naturel. Au bord de l’extinction, il ne subsistait plus que 12 individus lors de la mise en place d’un programme de conservation en 1998 par la [SOP Manu](https://www.manu.pf/). Avec environ 100 individus estimés en 2020, les effectifs de l’espèce sont en augmentation mais sa survie reste fragile.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80181, 1040, 'TEXT', 'statistics.3.number', 'Plus de 885', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80182, 1040, 'TEXT', 'species.1.description', 'Le Tiare Tahiti ([*Gardenia taitensis*](https://inpn.mnhn.fr/espece/cd_nom/447489)) est un arbuste dont les fleurs blanches odorantes sont utilisées pour la fabrication du *monoi* (huile de coprah issue de la noix de coco, dans laquelle sont macérées des fleurs de tiaré). Originaire des îles du Pacifique ouest, il a été introduit par les premiers Polynésiens (comme d’autres plantes cultivées telles que l’arbre à pain, le taro, le bambou, etc.) et est devenu l’une des espèces emblématiques de la Polynésie française.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81864, 1011, 'TEXT', 'presentation.descriptionTerritories', 'du territoire est occupé par des forêts', null, null, null, false);
