INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81868, 1011, 'IMAGE', 'presentation.image', null, 1377, 'Forêt tropicale humide, Montagne Trésor, Guyane  © Guillaume Feuillet - 97px', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81869, 1011, 'TEXT', 'understand.paragraphs.1.title', 'Des services écosystémiques et une valeur patrimoniale', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80817, 1041, 'TEXT', 'species.1.description', 'Avec une envergure pouvant atteindre 3,7 m, l’Albatros hurleur ([*Diomedea exulans*](https://inpn.mnhn.fr/espece/cd_nom/988)) est le plus grand oiseau au monde. Ses longues ailes rendent son envol un peu difficile mais elles lui permettent de planer sans effort sur de grandes distances. Il ne revient sur terre qu’une fois tous les 2 ans pour la saison de reproduction. L’Albatros d’Amsterdam ([*Diomedea amsterdamensis*](https://inpn.mnhn.fr/espece/cd_nom/669383)) est endémique strict de l’île Amsterdam et se reproduit uniquement sur un haut plateau de l’île, le Plateau des Tourbières. La petite taille de sa population, estimée à 210-220 individus, en fait une des espèces d’oiseaux les plus rares au monde, en danger critique d’extinction. Un plan national d’actions vise à sa préservation, sur terre comme en mer. Les 2/3 de la population mondiale d’Albatros à bec jaune ([*Thalassarche carteri*](https://inpn.mnhn.fr/espece/cd_nom/713021)) se reproduisent dans les Terres australes françaises. Sur la seule île Amsterdam, on trouve la plus grande colonie au monde concentrée sur les falaises d’Entrecasteaux, qui représente 60 % de la population mondiale.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80818, 1041, 'TEXT', 'species.2.description', 'Les Gorfous dorés ([*Eudyptes chrysolophus*](https://inpn.mnhn.fr/espece/cd_nom/441903)), aussi appelés Gorfous macaronis, sont de petits manchots reconnaissables à la touffe de plumes jaunes de chaque côté de leur tête, appelée « aigrette ». Contrairement aux manchots royaux qui nichent sur les plages, ils nichent dans les espaces rocheux difficiles d’accès, en compagnie des Gorfous sauteurs ([*Eudyptes chrysocome*](https://inpn.mnhn.fr/espece/cd_nom/441902)), plus petits et possédant un bec plus court.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80819, 1041, 'TEXT', 'species.9.description', 'La mouche [*Anatalanta aptera*](https://inpn.mnhn.fr/espece/cd_nom/782785), endémique de la région subantarctique, a une étrange particularité : elle n’a pas d’ailes ! Ce caractère morphologique est une adaptation aux conditions climatiques rigoureuses des Terres australes françaises et leurs vents forts et constants, ainsi qu’à l’absence de prédateurs natifs.
