INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80832, 1041, 'TEXT', 'species.4.description', 'Chassée pour sa fourrure, l’Otarie à fourrure d’Amsterdam ([*Arctocephalus tropicalis*](https://inpn.mnhn.fr/espece/cd_nom/528710)) a frôlé l’extinction au début du XXe siècle. Si les populations restent globalement inférieures au niveau de pré-exploitation, l’île Amsterdam abrite aujourd’hui la 3e plus grande population mondiale d’otarie d’Amsterdam.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80833, 1041, 'TEXT', 'events.6.description', 'Création du territoire des Terres Australes et Antarctiques françaises (TAAF), divisé en 3 districts', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80834, 1041, 'TEXT', 'zoom.callToAction', 'Cliquez sur la carte thématique pour vous repérer en un coup d''œil !', null, null, null, false);
