INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80853, 1041, 'TEXT', 'identity.presentation', 'Fjords, glaciers, falaises, cratères, vallées à la végétation rase… Les Terres Australes françaises– aussi appelées îles subantarctiques - fascinent par leur isolement et leur nature brute et inhospitalière. Au sud de l’océan Indien entre les 40<sup>e</sup> rugissants et les 50<sup>e</sup> hurlants, ces îles volcaniques balayées par les vents présentent des paysages exceptionnels et abritent l’une des plus fortes concentrations et diversités d’oiseaux marins au monde. Leurs eaux sont également très riches, accueillant de nombreux mammifères marins qui viennent s’y nourrir. Ces îles sont classées en Réserve naturelle nationale depuis 2006 et inscrites sur la Liste du Patrimoine mondial de l’Unesco depuis 2019. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80854, 1041, 'IMAGE', 'interests.locations.0.image', null, 1741, 'Albatros nicheur et falaises de Crozet © Julie Tucoulet', null, false);
