INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80203, 1040, 'TEXT', 'events.2.date', '300', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80204, 1040, 'TEXT', 'identity.presentation', 'À plusieurs milliers de kilomètres des continents les plus proches, la Polynésie française, avec ses 118 îles réparties en 5 archipels (Australes, Société, Marquises, Tuamotu, Gambier), s’étend sur une surface grande comme l’Europe. On trouve une grande diversité géomorphologique, des îles volcaniques hautes jeunes de moins d’un million d’années (Tahiti) aux atolls de plusieurs dizaines de millions d’années, ainsi qu’une grande amplitude climatique, allant du climat subtropical au quasi-équatorial. L’extrême isolement géographique des îles de la Polynésie française est à l’origine d’un taux d’endémisme particulièrement élevé de la faune et la flore terrestre, mais il rend aussi la gestion des espaces naturels plus difficile. La nature et la culture sont souvent indissociables dans les savoirs traditionnels du *fenua*.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80205, 1040, 'TEXT', 'identity.population', '279 300 ', null, null, null, false);
