INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80207, 1040, 'TEXT', 'risks.risks.2.name', 'Des statuts de protection variés pour les espaces et les espèces', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80208, 1040, 'TEXT', 'species.0.name', 'Le *Tiare ''apetahi*', null, null, null, false);
