INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80239, 1040, 'IMAGE', 'ecosystems.ecosystems.1.image', null, 1773, 'Prairie à *Paspalum vaginatum* à Huahine © Jean-Yves H. Meyer - Délégation à la Recherche de la Polynésie française', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80240, 1040, 'TEXT', 'interests.locations.3.description', 'Situées au sud-ouest de la Polynésie française, les Australes regroupent 7 petites îles, dont Rapa, avec une richesse floristique et faunistique parmi les plus extraordinaires du monde.', null, null, null, false);
