INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80249, 1040, 'TEXT', 'interests.locations.7.description', 'En marquisien *Fenua Enata*, la « Terre des Hommes », les Marquises regroupent 14 îles aux reliefs escarpés qui ne sont pas entourées par un récif de corail. Les écosystèmes marins des Marquises sont particulièrement riches en nutriments d’où la présence et l’abondance d’espèces remarquables comme les cétacés. La candidature des Marquises à l’inscription au patrimoine naturel et culturel de l’UNESCO est en cours d’instruction.', null, null, null, false);
