INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81873, 1011, 'TEXT', 'understand.paragraphs.0.text', 'La destruction des forêts est reconnue comme une pression majeure sur la biodiversité. Le suivi du taux de boisement permet de suivre l’évolution de cette pression et donc indirectement l’état de la biodiversité forestière. Sont exclues de ce calcul les forêts urbaines ou à vocation agricole.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81874, 1011, 'IMAGE', 'understand.image', null, 1378, 'Forêt boréale, Saint-Pierre-et-Miquelon ©  Bruno Letournel - OFB', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81875, 1011, 'TEXT', 'presentation.description', 'du territoire d’outre-mer est occupé par des forêts', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (81876, 1011, 'TEXT', 'presentation.logoUrl', 'https://naturefrance.fr/indicateurs/taux-de-boisement-dans-les-outre-mer', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80885, 1041, 'TEXT', 'species.0.description', 'Les îles australes, et l’archipel Crozet en particulier, accueillent les plus grandes populations de manchots royaux ([*Aptenodytes patagonicus*](https://inpn.mnhn.fr/espece/cd_nom/441650)) du monde. Leurs immenses colonies peuvent parfois atteindre plus d’un million d’individus. Excellent plongeur, le manchot royal se nourrit en mer de poissons-lanternes et de calmars. On le distingue du Manchot empereur ([*Aptenodytes forsteri*](https://inpn.mnhn.fr/espece/cd_nom/441649)), qui vit en Antarctique, par sa tâche auriculaire (autour de l’oreille) jaune en forme de cuillère. Le duvet du jeune manchot avant sa première mue est marron, ce qui lui vaut le surnom de « kiwi ».
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (80886, 1041, 'IMAGE', 'species.6.image', null, 1731, '*Pringlea antiscorbutica* © Julie Tucoulet', null, false);
