INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82782, 1019, 'IMAGE', 'examples.projects.4.image', null, 1346, '*Aspisoma ignitum* © Julien Touroult - INPN ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82783, 1019, 'TEXT', 'paragraphs.0.text', 'Certains programmes se basent sur l’application d’un **protocole précis** (durée d’observation, distance couverte, nombre de points d’écoute, etc.). Ils nécessitent souvent une formation à la méthode et une implication sérieuse et régulière, car la qualité des données récoltées dépend de la bonne mise en œuvre du protocole. C’est le cas par exemple du Suivi temporel des oiseaux communs (STOC).
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82784, 1019, 'TEXT', 'examples.projects.2.description', 'S’appuyer sur les observations de tous les usagers de la mer pour mieux comprendre le milieu marin mahorais, c’est l’objectif du programme TsiÔno, « J’ai vu » en shimaoré.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82785, 1019, 'LINK', 'examples.projects.2.more', 'En savoir plus', null, null, 'https://www.tsiono.fr/ ', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82786, 1019, 'TEXT', 'examples.projects.0.description', 'Développé par Kelonia, l’observatoire des tortues marines de La Réunion, pour contribuer à la compréhension de la biologie des tortues marines, ce programme invite les plongeurs à envoyer photos et observations du comportement des individus rencontrées. L''identification de chaque tortue repose sur la configuration unique des écailles de sa tête.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82787, 1019, 'TEXT', 'examples.projects.4.description', 'Relai guadeloupéen de l’Observatoire des vers luisants et lucioles, l’observatoire Ti bèt a limiè a commencé ses activités en 2019. Les participants sont invités à faire remonter leurs observations de plusieurs espèces d’insectes lumineux (lucioles et taupins), afin de mieux connaitre leur biologie et leur écologie, identifier les facteurs favorisant leur présence ou leur régression et sensibiliser la population à leur maintien.', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82788, 1019, 'IMAGE', 'images.1.image', null, 1352, 'Observations à la longue vue à Saint-Pierre et Miquelon © Nathalie De Lacoste', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82789, 1019, 'TEXT', 'examples.projects.3.description', 'Sur les côtes néo-calédoniennes, plus de 80 stations coraliennes sont suivies annuellement selon une méthodologie appliquée par le réseau international [Reef Check](https://www.reefcheck.fr). Les observations protocolées sont réalisées sur quatre compartiments de l''environnement récifal (habitats, poissons, macro-invertébrés et perturbations du récif) pour évaluer sont état de santé. L''association Pala Dalik assure l’animation, la coordination et la formation des participants sur l''ensemble des trois provinces. ', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82790, 1019, 'TEXT', 'examples.projects.2.actor', 'Parc marin de Mayotte', null, null, null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82791, 1019, 'LINK', 'examples.projects.4.more', 'En savoir plus', null, null, 'https://www.anbaloup-la.fr/observatoire-lucioles/', false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82792, 1019, 'TEXT', 'paragraphs.1.text', 'La plupart des programmes permettent d’alimenter des bases de données collaboratives. Certains projets sont adossés à des programmes de recherche et visent à répondre à une question précise. D’autres mettent en avant en premier lieu un fort objectif de pédagogie, d’acquisition de connaissances et de sensibilisation à la démarche scientifique des observateurs.
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82793, 1019, 'IMAGE', 'examples.projects.0.image', null, 1081, '© Cynomana  / CC BY-SA 4.0 ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82794, 1019, 'IMAGE', 'examples.projects.3.image', null, 1083, '© Reefcheck France ', null, false);
INSERT INTO page_element (id, page_id, type, key, text, image_id, alt, href, title) VALUES (82795, 1019, 'TEXT', 'examples.projects.3.target', 'Plongeurs, apnéistes. Formation nécessaire. ', null, null, null, false);
